frappe.ui.form.on('Sales Invoice', {
    // ---------- COST CENTER & LOCATION ----------
    cost_center(frm) {
        const cost_center = frm.doc.cost_center;

        if (cost_center) {
            frappe.db.get_value('Cost Center', cost_center, 'custom_location')
                .then(r => {
                    const loc = r?.message?.custom_location || '';
                    frm.set_value('location', loc);
                    frm.set_query('location', () => ({
                        filters: { name: loc }
                    }));
                });
        } else {
            frm.set_value('location', '');
            frm.set_query('location', () => ({}));
        }
    },

    location(frm) {
        frm.set_query('cost_center', () => {
            const filters = { is_group: 0 };
            if (frm.doc.location) filters.custom_location = frm.doc.location;
            return { filters };
        });
    },

    // ---------- ITEM CHILD TABLE RESTRICTION ----------
    onload(frm) {
        restrict_item_table(frm);
        if (frm.is_new()) {
            setTimeout(() => allow_first_item(frm), 500);
        }
    },

    refresh(frm) {
        restrict_item_table(frm);
        toggle_add_delete(frm);

        if (!window.purchaseItemWatcher) {
            window.purchaseItemWatcher = setInterval(() => {
                restrict_item_table(frm);
                toggle_add_delete(frm);
            }, 800);
        }
    },

    before_unload() {
        if (window.purchaseItemWatcher) {
            clearInterval(window.purchaseItemWatcher);
            window.purchaseItemWatcher = null;
        }
    }
});


// ---------- HELPER FUNCTIONS ----------
function restrict_item_table(frm) {
    frm.set_df_property('items', 'cannot_add_multiple', true);
    frm.set_df_property('items', 'allow_bulk_edit', false);
    $('.grid-add-multiple-rows, .btn-add-multiple-rows').hide();
}

function toggle_add_delete(frm) {
    const has_items = frm.doc.items?.length > 0;

    frm.set_df_property('items', 'cannot_add_rows', has_items);
    frm.set_df_property('items', 'cannot_delete_rows', !has_items);

    $('.grid-add-multiple-rows, .btn-add-multiple-rows').hide();
}

function allow_first_item(frm) {
    frm.set_df_property('items', 'cannot_add_rows', false);
    frm.set_df_property('items', 'cannot_add_multiple', true);
    frm.set_df_property('items', 'allow_bulk_edit', false);
    frm.set_df_property('items', 'cannot_delete_rows', true);
    frm.set_df_property('items', 'cannot_delete_all_rows', true);
    $('.grid-add-multiple-rows, .btn-add-multiple-rows').hide();
}


frappe.ui.form.on('Sales Invoice Item', {
    qty(frm) {
        restrict_item_table(frm);
        toggle_add_delete(frm);
    },

    after_delete(frm) {
        restrict_item_table(frm);
        toggle_add_delete(frm);
    },

    after_add(frm) {
        restrict_item_table(frm);
        toggle_add_delete(frm);
    },
    
    // added 
    item_code(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if(row.item_code) {
            frm.set_value('custom_item', row.item_code);
        }
    }
})

