frappe.ui.form.on('Payment Entry', {
    onload_post_render: function(frm) {
        setTimeout(function() {
            frm.trigger('fetch_custom_item');
        }, 500);  // Small delay to ensure everything is rendered
    },
    refresh: function(frm) {
        setTimeout(function() {
            frm.trigger('fetch_custom_item');
        }, 500);
    },
    references_add: function(frm) {
        setTimeout(function() {
            frm.trigger('fetch_custom_item');
        }, 100);
    },
    fetch_custom_item: function(frm) {
        console.log('Fetching custom item...');
        console.log('References:', frm.doc.references);
        if (frm.doc.references && frm.doc.references.length > 0) {
            let first_row = frm.doc.references.find(r => 
                (r.reference_doctype === 'Sales Invoice' || r.reference_doctype === 'Purchase Invoice') && r.reference_name
            );
            if (first_row) {
                console.log('Found reference:', first_row);
                frappe.model.get_value(first_row.reference_doctype, first_row.reference_name, 'custom_item', function(value) {
                    console.log('Fetched value:', value);
                    if (value && value.custom_item && !frm.doc.custom_item) {
                        frm.set_value('custom_item', value.custom_item);
                        console.log('Set custom_item to:', value.custom_item);
                    }
                });
            } else {
                console.log('No valid reference found');
            }
        } else {
            console.log('No references');
        }
    },
    // ---------- COST CENTER & LOCATION ----------
    cost_center(frm) {
        const cost_center = frm.doc.cost_center;

        if (cost_center) {
            frappe.db.get_value('Cost Center', cost_center, 'custom_location')
                .then(r => {
                    const loc = r?.message?.custom_location || '';
                    frm.set_value('location', loc);
                    frm.set_query('location', () => ({
                        filters: { name: loc }
                    }));
                });
        } else {
            frm.set_value('location', '');
            frm.set_query('location', () => ({}));
        }
    },

    location(frm) {
        frm.set_query('cost_center', () => {
            const filters = { is_group: 0 };
            if (frm.doc.location) filters.custom_location = frm.doc.location;
            return { filters };
        });
    },

    // ---------- PAYMENT REFERENCE CHILD TABLE RESTRICTION ----------
    onload(frm) {
        restrict_reference_table(frm);
        if (frm.is_new()) {
            setTimeout(() => allow_first_reference(frm), 500);
        }
    },

    refresh(frm) {
        restrict_reference_table(frm);
        toggle_add_delete_reference(frm);

        if (!window.referenceWatcher) {
            window.referenceWatcher = setInterval(() => {
                restrict_reference_table(frm);
                toggle_add_delete_reference(frm);
            }, 800);
        }
    },

    before_unload() {
        if (window.referenceWatcher) {
            clearInterval(window.referenceWatcher);
            window.referenceWatcher = null;
        }
    }
});

frappe.ui.form.on('Payment Entry Reference', {
    reference_name(frm, cdt, cdn) {
        restrict_reference_table(frm);
        toggle_add_delete_reference(frm);
    },

    allocated_amount(frm) {
        restrict_reference_table(frm);
        toggle_add_delete_reference(frm);
    },

    after_delete(frm) {
        restrict_reference_table(frm);
        toggle_add_delete_reference(frm);
    },

    after_add(frm) {
        restrict_reference_table(frm);
        toggle_add_delete_reference(frm);
    }
});

// ---------- HELPER FUNCTIONS ----------
function restrict_reference_table(frm) {
    frm.set_df_property('references', 'cannot_add_multiple', true);
    frm.set_df_property('references', 'allow_bulk_edit', false);
    $('.grid-add-multiple-rows, .btn-add-multiple-rows').hide();
}

function toggle_add_delete_reference(frm) {
    const has_refs = frm.doc.references?.length > 0;

    frm.set_df_property('references', 'cannot_add_rows', has_refs);
    frm.set_df_property('references', 'cannot_delete_rows', !has_refs);

    $('.grid-add-multiple-rows, .btn-add-multiple-rows').hide();
}

function allow_first_reference(frm) {
    frm.set_df_property('references', 'cannot_add_rows', false);
    frm.set_df_property('references', 'cannot_add_multiple', true);
    frm.set_df_property('references', 'allow_bulk_edit', false);
    frm.set_df_property('references', 'cannot_delete_rows', true);
    frm.set_df_property('references', 'cannot_delete_all_rows', true);
    $('.grid-add-multiple-rows, .btn-add-multiple-rows').hide();
}
